import React, { useContext } from 'react';
import { AuthContext } from '../context/AuthContext';
import { Box, Heading, Text, VStack, Avatar, Fade, useColorModeValue } from '@chakra-ui/react';

const Profile = () => {
  const { user } = useContext(AuthContext);
  const cardBg = useColorModeValue('white', 'gray.700');

  if (!user) return null;

  return (
    <Fade in={true}>
      <Box maxW="400px" mx="auto" mt={12} p={8} bg={cardBg} borderRadius="lg" boxShadow="xl" textAlign="center">
        <Avatar size="xl" name={user.name || user.username} mb={4} />
        <Heading size="md" mb={2}>{user.name || 'User'}</Heading>
        <VStack spacing={1} align="center">
          <Text color="gray.500">Username: {user.username}</Text>
          <Text color="gray.500">Email: {user.email}</Text>
          <Text color="gray.500">Contact: {user.contact_number}</Text>
        </VStack>
      </Box>
    </Fade>
  );
};

export default Profile;
