import React from 'react';
import { Box, Flex, Text, Button, Badge } from '@chakra-ui/react';

const TaskItem = ({ task, onComplete, onDelete }) => {
  return (
    <Box
      bg={task.is_completed ? 'gray.100' : 'white'}
      p={4}
      mb={3}
      borderRadius="md"
      boxShadow="sm"
      transition="all 0.2s"
      _hover={{ boxShadow: 'md', bg: 'gray.50' }}
      opacity={task.is_completed ? 0.7 : 1}
    >
      <Flex align="center" justify="space-between">
        <Box>
          <Text fontWeight="bold" fontSize="lg" textDecoration={task.is_completed ? 'line-through' : 'none'}>
            {task.title}
            {task.is_completed && (
              <Badge ml={2} colorScheme="green">Completed</Badge>
            )}
          </Text>
          <Text color="gray.600">{task.description}</Text>
        </Box>
        <Flex gap={2}>
          {!task.is_completed && (
            <Button colorScheme="blue" size="sm" onClick={() => onComplete(task.id)}>
              Complete
            </Button>
          )}
          <Button colorScheme="red" size="sm" onClick={() => onDelete(task.id)}>
            Delete
          </Button>
        </Flex>
      </Flex>
    </Box>
  );
};

export default TaskItem;
