import React, { useState, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import { login, register } from '../services/api';
import { AuthContext } from '../context/AuthContext';
import {
  Box, Button, Input, FormControl, FormLabel, Heading, VStack, useToast, Text, Flex
} from '@chakra-ui/react';

const Auth = () => {
  const { login: doLogin } = useContext(AuthContext);
  const [isLogin, setIsLogin] = useState(true);
  // Registration fields
  const [username, setUsername] = useState('');
  const [name, setName] = useState('');
  const [contact, setContact] = useState('');
  const [email, setEmail] = useState('');
  // Login fields
  const [identifier, setIdentifier] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();
  const toast = useToast();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    try {
      if (isLogin) {
        const res = await login(identifier, password);
        doLogin(res.data.token);
        toast({ title: 'Login successful', status: 'success', duration: 2000, isClosable: true });
        navigate('/');
      } else {
        await register(username, password, name, contact, email);
        toast({ title: 'Registration successful! Please login.', status: 'success', duration: 2000, isClosable: true });
        setIsLogin(true);
      }
    } catch (err) {
      setError(err.response?.data?.error || 'Invalid credentials');
      toast({ title: err.response?.data?.error || 'Error', status: 'error', duration: 2000, isClosable: true });
    }
  };

  return (
    <Flex minH="100vh" align="center" justify="center" bg="gray.50">
      <Box bg="white" p={8} rounded="lg" boxShadow="lg" w="100%" maxW="400px">
        <Heading mb={6} textAlign="center" color="brand.800">
          {isLogin ? 'Login' : 'Register'}
        </Heading>
        <form onSubmit={handleSubmit}>
          <VStack spacing={4} align="stretch">
            {isLogin ? (
              <>
                <FormControl isRequired>
                  <FormLabel>Username / Email / Contact</FormLabel>
                  <Input
                    placeholder="Enter username, email, or contact"
                    value={identifier}
                    onChange={(e) => setIdentifier(e.target.value)}
                  />
                </FormControl>
                <FormControl isRequired>
                  <FormLabel>Password</FormLabel>
                  <Input
                    type="password"
                    placeholder="Password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                  />
                </FormControl>
              </>
            ) : (
              <>
                <FormControl isRequired>
                  <FormLabel>Username</FormLabel>
                  <Input
                    placeholder="Username"
                    value={username}
                    onChange={(e) => setUsername(e.target.value)}
                  />
                </FormControl>
                <FormControl isRequired>
                  <FormLabel>Name</FormLabel>
                  <Input
                    placeholder="Full Name"
                    value={name}
                    onChange={(e) => setName(e.target.value)}
                  />
                </FormControl>
                <FormControl isRequired>
                  <FormLabel>Contact Number</FormLabel>
                  <Input
                    placeholder="Contact Number"
                    value={contact}
                    onChange={(e) => setContact(e.target.value)}
                  />
                </FormControl>
                <FormControl isRequired>
                  <FormLabel>Email</FormLabel>
                  <Input
                    type="email"
                    placeholder="Email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                  />
                </FormControl>
                <FormControl isRequired>
                  <FormLabel>Password</FormLabel>
                  <Input
                    type="password"
                    placeholder="Password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                  />
                </FormControl>
              </>
            )}
            {error && <Text color="red.500" textAlign="center">{error}</Text>}
            <Button colorScheme="blue" type="submit" w="full">
              {isLogin ? 'Login' : 'Register'}
            </Button>
            <Button
              variant="link"
              colorScheme="blue"
              onClick={() => setIsLogin(!isLogin)}
            >
              {isLogin ? 'Need an account? Register' : 'Already have an account? Login'}
            </Button>
          </VStack>
        </form>
      </Box>
    </Flex>
  );
};

export default Auth;
