import React, { useState } from 'react';
import { addTask } from '../services/api';
import { Box, Button, Input, Flex, useToast } from '@chakra-ui/react';

const TaskForm = ({ onTaskAdded }) => {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [loading, setLoading] = useState(false);
  const toast = useToast();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      await addTask(title, description);
      setTitle('');
      setDescription('');
      toast({ title: 'Task added!', status: 'success', duration: 1500, isClosable: true });
      if (onTaskAdded) onTaskAdded();
    } catch (err) {
      toast({ title: err.response?.data?.error || 'Error adding task', status: 'error', duration: 2000, isClosable: true });
    }
    setLoading(false);
  };

  return (
    <Box as="form" onSubmit={handleSubmit} mb={6}>
      <Flex gap={2}>
        <Input
          placeholder="Task Title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          required
        />
        <Input
          placeholder="Description"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
        />
        <Button colorScheme="green" type="submit" isLoading={loading}>
          Add
        </Button>
      </Flex>
    </Box>
  );
};

export default TaskForm;
